import './App.css';
import React, { useState, useEffect } from 'react';
import axios, { Axios } from 'axios';
//import fs from 'fs';
import ResponseQuestions from './ResponseElements/ResponseQuestions';


function ResponseApp() {
  const[questions, setQuestions] = useState([]);
  const addQuestionHandler = question => {
    setQuestions((prevQuestions) => {
      return [...prevQuestions, question ];
      console.log(question);
    });
  }
  useEffect(()=> {
    axios.get('http://localhost:3001/api/get').then((response)=>{
      setQuestions(JSON.parse(response.data))
      //console.log(response.data);
    })
  })
  const submitForm = () => {
    axios.post('http://localhost:3001/api/insert', {
      formData: questions
    }).then(() => {
      alert('successful insert')
    });
  }
  return (
    <div className="App">
      <header>Form Submitter</header>
      <ResponseQuestions data={questions} ></ResponseQuestions>
      <button onClick={submitForm}>Submit Form</button>
    </div>
  );
}

export default ResponseApp;
